from django.conf.urls import urlfrom . import viewsurlpatterns = [    # 商品列表界面    url(r'^list/(?P<category_id>\d+)/(?P<page_num>\d+)/$', views.ListView.as_view()),    # 热销排序    url(r'^hot/(?P<category_id>\d+)/$', views.HotGoodsView.as_view()),    # 商品详情    url(r'^detail/(?P<sku_id>\d+)/$', views.DetailView.as_view()),    # 记录商品详情的访问量    url(r'^visit/(?P<category_id>\d+)/$', views.DetailVisitView.as_view()),    # 积分换购商品    url(r'^goods/integral/(?P<page_num>\d+)/$', views.GoodsIntegral.as_view()),    # 详情展示评价    url(r'^comments/(?P<sku_id>\d+)/$', views.CommentView.as_view()),]