from django.conf.urls import urlfrom . import viewsurlpatterns = [    # 用户注册    url(r'^register/$', views.RegisterView.as_view()),    # 判断用户名是否重复    url(r'^usernames/(?P<username>[a-zA-Z0-9_-]{5,20})/count/$', views.UsernameCountView.as_view()),    # 判断手机号是否重复    url(r'^mobiles/(?P<mobile>1[3-9]\d{9})/count/$', views.MobileCountView.as_view()),    # 用户登录    url(r'^login/$', views.LoginView.as_view()),    # 用户退出登录    url(r'^logout/$', views.LogoutView.as_view()),    # 用户中心    url(r'^info/$', views.InfoView.as_view()),    # 设置邮箱    url(r'^emails/$', views.EmailView.as_view()),    # 激活邮箱    url(r'^emails/verification/$', views.EmailVerificationView.as_view()),    # 收货地址展示    url(r'^addresses/$', views.AddressView.as_view()),    # 新增收货地址    url(r'^addresses/create/$', views.CreateAddressView.as_view()),    # 修改和删除收货地址    url(r'^addresses/(?P<address_id>\d+)/$', views.UpdateDestroyAddressView.as_view()),    # 修改收货地址标题    url(r'^addresses/(?P<address_id>\d+)/title/$', views.UpdateAddressTitleView.as_view()),    # 设置默认收货地址    url(r'^addresses/(?P<address_id>\d+)/default/$', views.DefaultAddressView.as_view()),    # 修改用户密码    url(r'^password/$', views.ChangePasswordView.as_view()),    # 保存用户的浏览记录    url(r'^browse_histories/$', views.UserBrowseHistory.as_view()),    # 忘记密码页面    url(r'^find_password/$', views.FindPasswordView.as_view()),    # 忘记密码--修改密码    url(r'^users/(?P<user_id>\d+)/password/$', views.FindPasswordView.as_view()),    # 全部订单    url(r'^orders/info/(?P<page_num>\d+)/$', views.AllOrdersView.as_view()),    # 商品评价    url(r'^orders/comment/$', views.GoodsJudgeView.as_view()),    # 注销帐户    url(r'^unsubscribe/$', views.UnsubscribeView.as_view()),    # 注销帐户--确认用户密码    url(r'^uusers/(?P<user_id>\d+)/password/$', views.UnsubscribeView.as_view()),]