from django.conf.urls import urlfrom . import viewsurlpatterns = [    # 获取图形验证码    url(r'^image_codes/(?P<uuid>[\w-]+)/$', views.ImageCodeView.as_view()),    # 获取短信验证码    url(r'^sms_codes/(?P<mobile>1[3-9]\d{9})/$', views.SMSCodeView.as_view()),    # 忘记密码    url(r'^accounts/(?P<username>[a-zA-Z0-9_-]{5,20})/sms/token/$', views.FindPasswordView.as_view()),    # 忘记密码中获取短信验证码    url(r'^sms_codes/$', views.FindSMSCodeView.as_view()),    # 验证短信验证码    url(r'^accounts/(?P<username>[a-zA-Z0-9_-]{5,20})/password/token/$', views.VerificationSMSCodeView.as_view())]